package com.example.controller;import com.alibaba.fastjson.JSON;import com.example.producer.KfkaProducer;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @author 朱朝阳 * @date 2019/11/21 20:28 */@RestController@RequestMapping("/kafka")public class SendKafkaMessage {    @Autowired    private KfkaProducer kfkaProducer;    @GetMapping    public Object sendKafkaMessage() {        kfkaProducer.sendToLocalKafkaServer();        return JSON.parse("{\"result\":\"ok\"}");    }}