package com.example.receiver;import lombok.extern.slf4j.Slf4j;import org.apache.kafka.clients.consumer.ConsumerRecord;import org.springframework.kafka.annotation.KafkaListener;import org.springframework.stereotype.Component;import java.util.Optional;/** * @author 朱朝阳 * Kafka消费者 * @date 2019/11/21 20:25 */@Slf4j@Componentpublic class KafkaReceiver {    @KafkaListener(topics = {"hello"})    public void listen(ConsumerRecord<?, ?> record) {        Optional<?> kafkaMessage = Optional.ofNullable(record.value());        if (kafkaMessage.isPresent()) {            Object message = kafkaMessage.get();            log.info("----------------- record =" + record);            log.info("------------------ message =" + message);        }    }}